import { Component } from '@angular/core';
import { NavController } from '@ionic/angular';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
  standalone: false,
})
export class HomePage {
  constructor(private navCtrl: NavController) {}

  async ionViewDidEnter() {
    // Simula la animación del logo
    setTimeout(async () => {
      if (navigator.onLine) {
        // Redirige al login si hay conexión
        this.navCtrl.navigateForward('/login');
      } else {
        alert('No hay conexión a Internet. Inténtalo de nuevo.');
      }
    }, 3000); // Tiempo de la animación (3 segundos)
  }
}


<ion-content [fullscreen]="true" class="splash-page">
  <div class="splash-container">
    <!-- Logo con Animación -->
    <img src="assets/logo.png" alt="Logo" class="logo animated-logo" />
    <h1 style="font-weight: bold;">Biblioteca Personal</h1>
    
    <h3><p style="font-weight: bold;">Alumno</p>Francis Fiallos</h3>
    
  </div>
</ion-content>
